import { useState, useRef, useContext } from "react";
import AuthContext from "../../store/auth-context";
import classes from "./AuthForm.module.css";
import { useHistory } from "react-router-dom";
const AddProduct = () => {
  const picInputRef = useRef();
  const nameInputRef = useRef();
  const descriptionInputRef = useRef();
  const priceInputRef = useRef();

  const authCtx = useContext(AuthContext);
  const history = useHistory();



  const submitHandler = (event) => {
    event.preventDefault();

    const enteredPic = picInputRef.current.value;
    const enteredName = nameInputRef.current.value;
    const enteredDescription = descriptionInputRef.current.value;
    const enteredPrice = priceInputRef.current.value;

    // optional: Add validation

    if (isLogin) {
      fetch("http://localhost:8080/api/users/login", {
        method: "POST",
        mode: "cors",
        body: JSON.stringify({
          username: enteredUsername,
          password: enteredPassword,
        }),
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
             res.json().then((data) => {
              console.log(data);
              authCtx.login(data);
              const role = data.role;
              if (role === "customer") {
                history.replace("/customer");
              } else {
                history.replace("/admin");
              }
            
          });
          } else {
            alert("username and password incorrect.");
          }
        })
        
    } else {
    
      fetch("http://localhost:8080/api/products", {
        method: "POST",
        mode: "cors",
        body: JSON.stringify({
          pic: enteredPic,
          name: enteredName,
          description: enteredDescription,
          price: enteredPrice,
        
        }),
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      }).then((res) => {
        if (res.ok) {
          alert("Successfully Registered");
        } else {
          alert("username already exists");
        }
      });
    }
  };

  return (
    <div className={classes.back}>
      <section className={classes.auth}>
        <h1>Add Product</h1>
        <form onSubmit={submitHandler}>
          <div className={classes.control}>
            <label htmlFor="pic">Username</label>
            <input type="file" id="pic" required ref={picInputRef} />
          </div>

          <div className={classes.control}>
            <label htmlFor="name">Product Name</label>
            <input
              type="text"
              id="name"
              required
              ref={nameInputRef}
            />
          </div>
          <div className={classes.control}>
            <label htmlFor="description">Product Description</label>
            <input
              type="text"
              id="n"
              required
              ref={nameInputRef}
            />
          </div>
          <div className={classes.control}>
            <label htmlFor="price">Product Price</label>
            <input
              type="text"
              id="name"
              required
              ref={nameInputRef}
            />
          </div>
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="firstname">First Name</label>
              <input
                type="text"
                id="firstname"
                required
                ref={firstNameInputRef}
              />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="lastname">Last Name</label>
              <input
                type="text"
                id="lastname"
                required
                ref={lastNameInputRef}
              />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="house">House Number</label>
              <input type="text" id="house" required ref={houseInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="street">Street</label>
              <input type="text" id="street" required ref={streetInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="city">City</label>
              <input type="text" id="city" required ref={cityInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="state">State</label>
              <input type="text" id="state" required ref={stateInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="pincode">Pincode</label>
              <input type="text" id="pincode" required ref={pincodeInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="phone">Phone</label>
              <input type="text" id="phone" required ref={phoneInputRef} />
            </div>
          )}
          <div className={classes.actions}>
            <button>{isLogin ? "Login" : "Create Account"}</button>
            <button
              type="button"
              className={classes.toggle}
              onClick={switchAuthModeHandler}
            >
              {isLogin ? "Create new account" : "Login with existing account"}
            </button>
          </div>
        </form>
      </section>
    </div>
  );
};

export default AddProduct;
