import { useContext,useState } from 'react';

import MealItemForm from './MealItemForm';
import classes from './MealItem.module.css';
import CartContext from '../../store/cart-context';

const OrderItem = (props) => {
  const cartCtx = useContext(CartContext);
  const [product, setProduct] = useState([]);

  useEffect(() => {
    const fetchProduct = async () => {
      const response = await fetch('http://localhost:8080/api/products/{props.productId}',{
        method: "GET",
        mode: "cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      });
      const responseData = await response.json();

      setProduct(responseData);

    };

    fetchProduct();
  }, [p]);



  const addToCartHandler = amount => {
    cartCtx.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price
    });
  };
  const getImageSrc = (byteArray) => {
    var img = null;
    if (byteArray != null) img = new Buffer.from(byteArray).toString("base64");
    const file = "data:image/jpeg;base64," + img;
    return file;
  };

  return (
    <li className={classes.meal}>
      <div>
        <h3>{props.productId}</h3>

        <div className={classes.description}>{props.quantity}</div>
        <div className={classes.price}>{props.status}</div>

      </div>
      <div>
        <MealItemForm id={props.id} onAddToCart={addToCartHandler} />
      </div>
    </li>
  );
};

export default OrderItem;
