import { useContext,useState,useEffect } from 'react';

import MealItemForm from './MealItemForm';
import classes from './MealItem.module.css';
import CartContext from '../../store/cart-context';

const OrderItem = (props) => {
  const cartCtx = useContext(CartContext);
  const [product, setProduct] = useState([]);

  useEffect(() => {
    const fetchProduct = async () => {
      const pId= Number(props.productId);
      const response = await fetch('http://localhost:8080/api/products/' + pId,{
        method: "GET",
        mode: "cors",
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      });
      const responseData = await response.json();
       console.log(responseData);
      setProduct(responseData);

    };

    fetchProduct();
  }, [product]);



  const clickHandler = amount => {
    cartCtx.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price
    });
  };
  const getImageSrc = (byteArray) => {
    var img = null;
    if (byteArray != null) img = new Buffer.from(byteArray).toString("base64");
    const file = "data:image/jpeg;base64," + img;
    return file;
  };
  let showCancelButton= false;
  if(props.status==='placed'){
    showCancelButton= true;
  }
  return (
    <li className={classes.meal}>
      <div>
        <div className={classes.price}>{product.name}</div>
        <img  className={classes.image} width="200" height="200" src={getImageSrc(product.pic)} />
        <div className={classes.description}>Amount x{props.quantity}</div>
        <div className={classes.price}>Status- {props.status}</div>
        <div className={classes.price}>Price-  {product.price * props.quantity}</div>
        {showCancelButton && <button className={classes.button} onClick={clickHandler}>Cancel</button>}
      </div>
    </li>
  );
};

export default OrderItem;
