import { useState,useRef } from 'react';

import classes from './AuthForm.module.css';

const AuthForm = () => {

  const usernameInputRef = useRef();
  const passwordInputRef = useRef();
  const firstNameInputRef = useRef();
  const lastNameInputRef = useRef();
  const houseInputRef = useRef();
  const streetInputRef = useRef();
  const cityInputRef = useRef();
  const stateInputRef = useRef();
  const pincodeInputRef = useRef();
  const phoneInputRef = useRef();

  const [isLogin, setIsLogin] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  return (
    <div  className={classes.back}>
    <section className={classes.auth}>
      <h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
      <form>
        <div className={classes.control}>
          <label htmlFor='email'>Username</label>
          <input type='email' id='email' required  ref={usernameInputRef}/>
        </div>
        <div className={classes.control}>
          <label htmlFor='password'>Password</label>
          <input type='password' id='password' required ref={passwordInputRef} />
        </div>
        {!isLogin && <div className={classes.control}>
          <label htmlFor='firstname'>First Name</label>
          <input type='text' id='firstname' required ref={firstNameInputRef} />
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='lastname'>Last Name</label>
          <input type='text' id='lastname' required ref={lastNameInputRef}/>
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='house'>House Number</label>
          <input type='text' id='house' required ref={houseInputRef}/>
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='street'>Street</label>
          <input type='text' id='street' required ref={streetInputRef}/>
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='city'>City</label>
          <input type='text' id='city' required ref={InputRef}/>
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='state'>State</label>
          <input type='text' id='state' required />
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='pincode'>Pincode</label>
          <input type='text' id='pincode' required />
        </div>}
        {!isLogin && <div className={classes.control}>
          <label htmlFor='phone'>Phone</label>
          <input type='text' id='phone' required />
        </div>}
        <div className={classes.actions}>
          <button>{isLogin ? 'Login' : 'Create Account'}</button>
          <button
            type='button'
            className={classes.toggle}
            onClick={switchAuthModeHandler}
          >
            {isLogin ? 'Create new account' : 'Login with existing account'}
          </button>
        </div>
      </form>
    </section>
    </div>
  );
};

export default AuthForm;
