import { Fragment,useContext } from 'react';
import AuthContext from '../../store/auth-context';
import HeaderCartButton from './HeaderCartButton';
import shopping from '../../assets/shopping.jpeg';
import classes from './Header.module.css';
import { Route} from "react-router-dom";
import Orders from './Orders';
const Header = (props) => {
  const authCtx = useContext(AuthContext);
  const logoutHandler = () =>{
    authCtx.logout();

  }
  const myOrders = () =>{
    <Route path="/customer/orders">
   <Orders />
</Route>
  }
  return (
    <Fragment>
      <header className={classes.header}>
        <h1>oKart</h1>
        <button align="right" onClick={myOrders} className={classes.button}>Orders</button>
        <button className={classes.button}>Add Product</button>
        <HeaderCartButton onClick={props.onShowCart} />
        <button align="right" onClick={logoutHandler} className={classes.button}>Logout</button>
      </header>
      <div className={classes['main-image']}>
        <img src={shopping} alt='A table full of delicious food!' />
      </div>

    </Fragment>
  );
};

export default Header;
