import { useState, useRef, useContext } from "react";
import AuthContext from "../../store/auth-context";
import classes from "./AuthForm.module.css";
import { useHistory } from "react-router-dom";
const AuthForm = () => {
  const usernameInputRef = useRef();
  const passwordInputRef = useRef();
  const firstNameInputRef = useRef();
  const lastNameInputRef = useRef();
  const houseInputRef = useRef();
  const streetInputRef = useRef();
  const cityInputRef = useRef();
  const stateInputRef = useRef();
  const pincodeInputRef = useRef();
  const phoneInputRef = useRef();

  const authCtx = useContext(AuthContext);
  const history = useHistory();
  const [isLogin, setIsLogin] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const enteredUsername = usernameInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;

    // optional: Add validation

    if (isLogin) {
      fetch("http://localhost:8080/api/users/login", {
        method: "POST",
        mode: "cors",
        body: JSON.stringify({
          username: enteredUsername,
          password: enteredPassword,
        }),
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      })
        .then((res) => {
          if (res.ok) {
            return res.json();
          } else {
            alert("username and password incorrect.");
          }
        })
        
    } else {
      const enteredFirstName = firstNameInputRef.current.value;
      const enteredLastName = lastNameInputRef.current.value;
      const enteredHouse = houseInputRef.current.value;
      const enteredStreet = streetInputRef.current.value;
      const enteredCity = cityInputRef.current.value;
      const enteredState = stateInputRef.current.value;
      const enteredPincode = pincodeInputRef.current.value;
      const enteredPhone = phoneInputRef.current.value;
      fetch("http://localhost:8080/api/users", {
        method: "POST",
        mode: "cors",
        body: JSON.stringify({
          username: enteredUsername,
          password: enteredPassword,
          firstName: enteredFirstName,
          lastName: enteredLastName,
          houseNo: enteredHouse,
          street: enteredStreet,
          city: enteredCity,
          state: enteredState,
          pincode: enteredPincode,
          phone: enteredPhone,
          role: "customer",
        }),
        headers: {
          "Access-Control-Allow-Origin": "*",
          "Content-Type": "application/json",
        },
      }).then((res) => {
        if (res.ok) {
          alert("Successfully Registered");
        } else {
          alert("username already exists");
        }
      });
    }
  };

  return (
    <div className={classes.back}>
      <section className={classes.auth}>
        <h1>{isLogin ? "Login" : "Sign Up"}</h1>
        <form onSubmit={submitHandler}>
          <div className={classes.control}>
            <label htmlFor="email">Username</label>
            <input type="text" id="email" required ref={usernameInputRef} />
          </div>

          <div className={classes.control}>
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              required
              ref={passwordInputRef}
            />
          </div>
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="firstname">First Name</label>
              <input
                type="text"
                id="firstname"
                required
                ref={firstNameInputRef}
              />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="lastname">Last Name</label>
              <input
                type="text"
                id="lastname"
                required
                ref={lastNameInputRef}
              />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="house">House Number</label>
              <input type="text" id="house" required ref={houseInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="street">Street</label>
              <input type="text" id="street" required ref={streetInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="city">City</label>
              <input type="text" id="city" required ref={cityInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="state">State</label>
              <input type="text" id="state" required ref={stateInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="pincode">Pincode</label>
              <input type="text" id="pincode" required ref={pincodeInputRef} />
            </div>
          )}
          {!isLogin && (
            <div className={classes.control}>
              <label htmlFor="phone">Phone</label>
              <input type="text" id="phone" required ref={phoneInputRef} />
            </div>
          )}
          <div className={classes.actions}>
            <button>{isLogin ? "Login" : "Create Account"}</button>
            <button
              type="button"
              className={classes.toggle}
              onClick={switchAuthModeHandler}
            >
              {isLogin ? "Create new account" : "Login with existing account"}
            </button>
          </div>
        </form>
      </section>
    </div>
  );
};

export default AuthForm;
